import React, {useState} from "react";
import TodoItem from "./item/TodoItem";

const data = [
    {
        id: '1',
        title: 'front',
        isCompleted: false,
    },
    {
        id: '2',
        title: 'web',
        isCompleted: false,
    },
    {
        id: '3',
        title: 'flutter',
        isCompleted: false,
    },
    {
        id: '4',
        title: 'front x2',
        isCompleted: false,
    },

]

const Home = () => {
    const [todos, setTodos] = useState(data)

     const changeTodo = (id) => {
        const copy = [...todos]
        const current = copy.find(todot => todot.id === id)
        current.isCompleted = !current.isCompleted
        setTodos(copy)
     }

    const removeTodo = (id) => {

        setTodos([...todos].filter(todot => todot.id !== id))
    }

    const addTodo = (title) => {
        setTodos([{
            id: new Date(),
            title: title,
            isCompleted: false
        },
        ...todos])
    }

    return (
        <div className='w-4/5 mx-auto'>
            <h1 className='text-2x1 font-bold text-center mb-8 '>home</h1>

            {
                todos.map(todo => (
                    <TodoItem key={todo.id} todo={todo} changeTodo={changeTodo} removeTodo={removeTodo}/>
                ))
            }
            
        </div>
    )
}

export default Home
